module.exports = {
    hooks: {
        "page:before": function (page) {
            var config = this.config.get('pluginsConfig.last-modified');
            console.log(config.xxx)
            if (config !== undefined && config.ignore !== undefined && config.ignore.indexOf(page.path) !== -1) {
                return page;
            }
            let content = page.content;
            content = `>*Last modified111: {{ file.mtime }}*\n\n${content}`; 
            page.content = content; 
            return page;
        }
    }
};
